package org.example.Multithread.Lock;public class BankAccount {    private int balance = 1000;    public synchronized void withdraw(int amount) throws InterruptedException {        System.out.println(Thread.currentThread().getName()+"attempting to withdraw "+amount);        if(balance>=amount){            Thread.sleep(10000);            balance = balance -amount;            System.out.println("Balance  : "+(balance-amount));        }        else {            System.out.println("Insufficient Balance ");        }    }}