package org.example.Multithread.Methods;public class PriorityAssignment extends Thread{    public PriorityAssignment(String name) {        super(name);    }    @Override    public void run() {        for(int i = 0;i<5;i++){            System.out.println("The Thread is "+Thread.currentThread().getName() +" has priority "+                    Thread.currentThread().getPriority());            try {                Thread.sleep(100);            } catch (InterruptedException e) {                throw new RuntimeException(e);            }        }    }    public static void main(String[] args) {        PriorityAssignment lowp = new PriorityAssignment("Low Priority");        PriorityAssignment medp = new PriorityAssignment("Medium Priority");        PriorityAssignment highp = new PriorityAssignment(" High Priority");        lowp.setPriority(Thread.MIN_PRIORITY);        medp.setPriority(Thread.NORM_PRIORITY);        highp.setPriority(Thread.MAX_PRIORITY);        lowp.start();        medp.start();        highp.start();    }}